#Author: Brian Weinstein
#Sources: stackoverflow, rubydoc

def palindrome?(string)
	#remove all non-alphanumeric characters.
	@letters = string.gsub(/\W/, ' ')
	#make string lower case
	@lowcase = @letters.downcase
	#reverse string
	@reverse = @lowcase.reverse
	
	#check if palindrome
	if (@reverse == @lowcase)
		true
	end
	false
end

if (palindrome?("A man, a plan, a canal -- Panama"))
	puts "Failed. Palindrome"
end

if (palindrome?("Madam, I'm Adam!"))
	puts "Failed. Palindrome"
end

if (palindrome?("Abracadabra"))
	puts "Failed. No Palindrome."
end

